                else if (char == 0x0c) output[outputLength++] = "f";
                else if (char == 0x0d) output[outputLength++] = "r";
                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(buffer, add(0x20, offset)))
        }
    }
}

contracts/content/EncryptedArticles.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

interface IPublisherCredentials {
    function balanceOf(address owner) external view returns (uint256);
}

interface IMembershipTokens {
    function balanceOf(address owner) external view returns (uint256);
    function getProfileLocation(address owner) external view returns (string memory);
}

interface IReaderLicenseAMM {
    function mintLicensesToHolder(uint256 articleId, address holder, uint256 count, uint256 editionNumber) external;
}

contract EncryptedArticles is ERC721, ERC721URIStorage, ERC721Royalty, Ownable {
    using Strings for uint256;

    // Constants
    uint256 public constant PUBLISHING_FEE = 1 ether; // 1 FLOW
    uint256 public constant BUYER_FEE = 1 ether; // 1 FLOW buyer fee
    uint256 public constant PLATFORM_TAKE_RATE = 190; // 1.9% (basis points: 190/10000)
    uint256 public constant DAILY_NATIVE_LIMIT = 3; // Max 3 native articles per day
    uint256 public constant MAX_TITLE_LENGTH = 200;
    uint256 public constant MAX_SUMMARY_LENGTH = 500;
    uint256 public constant MAX_CONTENT_LENGTH = 25000; // ~25KB of text
    uint256 public constant MAX_TAGS = 10;
    uint256 public constant MAX_TAG_LENGTH = 50;

    // Contract addresses
    address public constant TREASURY = 0x9402F9f20b4a27b55B1cC6cf015D98f764814fb2;
    IPublisherCredentials public immutable PUBLISHER_CREDENTIALS;
    IMembershipTokens public immutable MEMBERSHIP_TOKENS;
    IReaderLicenseAMM public READER_LICENSE_AMM;

    // Enums
    enum CreationSource { NATIVE, PROPOSAL }

    // Structs
    struct Article {
        uint256 id;
        string title;
        string encryptedContent;
        string summary;
        address author;
        string location;
        string category;
        string[] tags;
        uint256 publishedAt;
        uint256 nftCount;
        uint256 nftPrice;
        uint256 journalistRetained;
        uint256 readerLicenseRatio;
        CreationSource creationSource;
        uint256 proposalId;
    }

    // State variables
    uint256 private _articleIdCounter;
    uint256 private _tokenIdCounter;

    mapping(uint256 => Article) public articles;
    mapping(uint256 => uint256) public tokenToArticle; // NFT tokenId => articleId
    mapping(uint256 => uint256) public tokenToEdition; // NFT tokenId => edition number
    mapping(address => mapping(uint256 => uint256)) public dailyPublishCount; // publisher => day => count
    mapping(uint256 => address[]) public articleNFTHolders; // articleId => holders array
    mapping(uint256 => mapping(address => uint256)) public holderNFTCount; // articleId => holder => count
    mapping(uint256 => uint256[]) public availableEditions; // articleId => available edition numbers
    mapping(uint256 => uint256) public editionsSold; // articleId => count of editions sold

    // Events
    event ArticlePublished(
        uint256 indexed articleId,
        address indexed author,
        string title,
        string location,
        uint256 nftCount,
        uint256 nftPrice
    );

    event NFTMinted(
        uint256 indexed tokenId,
        uint256 indexed articleId,
        address indexed buyer,
        uint256 editionNumber,
        uint256 price,
        uint256 licensesGenerated
    );

    constructor(
    address _publisherCredentials,
    address _membershipTokens
) ERC721("ImmutableType Encrypted Articles", "ITEA") Ownable(msg.sender) {
        PUBLISHER_CREDENTIALS = IPublisherCredentials(_publisherCredentials);
        MEMBERSHIP_TOKENS = IMembershipTokens(_membershipTokens);

        // Set default royalty to 4.9% (490 basis points)
        _setDefaultRoyalty(address(this), 490);
    }

    // Modifiers
    modifier onlyPublisher() {
        require(PUBLISHER_CREDENTIALS.balanceOf(msg.sender) > 0, "Publisher credentials required");
        _;
    }

    modifier onlyLocalPublisher(string memory articleLocation) {
        string memory publisherLocation = MEMBERSHIP_TOKENS.getProfileLocation(msg.sender);
        require(
            keccak256(abi.encodePacked(publisherLocation)) == keccak256(abi.encodePacked(articleLocation)),
            "Must have local token for this geography"
        );
        _;
    }

    modifier onlyLocalMember(string memory articleLocation) {
        require(MEMBERSHIP_TOKENS.balanceOf(msg.sender) > 0, "Membership token required");
        string memory memberLocation = MEMBERSHIP_TOKENS.getProfileLocation(msg.sender);
        require(
            keccak256(abi.encodePacked(memberLocation)) == keccak256(abi.encodePacked(articleLocation)),
            "Must have local token for this geography"
        );
        _;
    }

    modifier withinDailyLimit() {
        uint256 today = block.timestamp / 86400; // Current day
        require(
            dailyPublishCount[msg.sender][today] < DAILY_NATIVE_LIMIT,
            "Daily native article limit exceeded"
        );
        _;
    }

    // Set Reader License AMM address (only owner)
    function setReaderLicenseAMM(address _ammAddress) external onlyOwner {
        READER_LICENSE_AMM = IReaderLicenseAMM(_ammAddress);
    }

    // Publish encrypted article
    function publishArticle(
        string memory _title,
        string memory _encryptedContent,
        string memory _summary,
        string memory _location,
        string memory _category,
        string[] memory _tags,
        uint256 _nftCount,
        uint256 _nftPrice,
        uint256 _journalistRetained,
        uint256 _readerLicenseRatio
    ) external payable onlyPublisher onlyLocalPublisher(_location) withinDailyLimit {
        require(msg.value >= PUBLISHING_FEE, "Insufficient publishing fee");
        require(bytes(_title).length > 0 && bytes(_title).length <= MAX_TITLE_LENGTH, "Invalid title length");
        require(bytes(_encryptedContent).length > 0 && bytes(_encryptedContent).length <= MAX_CONTENT_LENGTH, "Invalid content length");
        require(bytes(_summary).length > 0 && bytes(_summary).length <= MAX_SUMMARY_LENGTH, "Invalid summary length");
        require(_nftCount > 0 && _nftCount <= 10000, "Invalid NFT count");
        require(_nftPrice > 0, "Invalid NFT price");
        require(_journalistRetained <= _nftCount, "Cannot retain more than total NFTs");
        require(_readerLicenseRatio > 0 && _readerLicenseRatio <= 100, "Invalid license ratio");
        require(_tags.length <= MAX_TAGS, "Too many tags");

        // Validate tags
        for (uint256 i = 0; i < _tags.length; i++) {
            require(bytes(_tags[i]).length <= MAX_TAG_LENGTH, "Tag too long");
        }

        uint256 articleId = ++_articleIdCounter;

        // Store article
        articles[articleId] = Article({
            id: articleId,
            title: _title,
            encryptedContent: _encryptedContent,
            summary: _summary,
            author: msg.sender,
            location: _location,
            category: _category,
            tags: _tags,
            publishedAt: block.timestamp,
            nftCount: _nftCount,
            nftPrice: _nftPrice,
            journalistRetained: _journalistRetained,
            readerLicenseRatio: _readerLicenseRatio,
            creationSource: CreationSource.NATIVE,
            proposalId: 0
        });

        // Initialize available editions (exclude journalist retained editions)
        uint256 publicSaleCount = _nftCount - _journalistRetained;
        for (uint256 i = _journalistRetained + 1; i <= _nftCount; i++) {
            availableEditions[articleId].push(i);
        }

        // Update daily count
        uint256 today = block.timestamp / 86400;
        dailyPublishCount[msg.sender][today]++;

        // Send publishing fee to treasury
        payable(TREASURY).transfer(PUBLISHING_FEE);

        // Refund excess
        if (msg.value > PUBLISHING_FEE) {
            payable(msg.sender).transfer(msg.value - PUBLISHING_FEE);
        }

        emit ArticlePublished(articleId, msg.sender, _title, _location, _nftCount, _nftPrice);
    }

    // Purchase NFT edition (local members only)
    function mintNFTEdition(uint256 _articleId) external payable {
        Article storage article = articles[_articleId];
        require(article.id != 0, "Article does not exist");
        require(holderNFTCount[_articleId][msg.sender] == 0, "Already owns NFT for this article");
        require(availableEditions[_articleId].length > 0, "No editions available for public sale");

        // Check local membership
        string memory articleLocation = article.location;
        require(MEMBERSHIP_TOKENS.balanceOf(msg.sender) > 0, "Membership token required");
        string memory buyerLocation = MEMBERSHIP_TOKENS.getProfileLocation(msg.sender);
        require(
            keccak256(abi.encodePacked(buyerLocation)) == keccak256(abi.encodePacked(articleLocation)),
            "Must have local token to purchase NFTs"
        );

        uint256 totalPrice = article.nftPrice + BUYER_FEE;
        require(msg.value >= totalPrice, "Insufficient payment");

        // Get random edition number
        uint256 randomIndex = _getRandomIndex(_articleId) % availableEditions[_articleId].length;
        uint256 editionNumber = availableEditions[_articleId][randomIndex];

        // Remove edition from available list
        availableEditions[_articleId][randomIndex] = availableEditions[_articleId][availableEditions[_articleId].length - 1];
        availableEditions[_articleId].pop();

        uint256 tokenId = ++_tokenIdCounter;

        // Mint NFT
        _mint(msg.sender, tokenId);
        tokenToArticle[tokenId] = _articleId;
        tokenToEdition[tokenId] = editionNumber;

        // Update holder tracking
        if (holderNFTCount[_articleId][msg.sender] == 0) {
            articleNFTHolders[_articleId].push(msg.sender);
        }
        holderNFTCount[_articleId][msg.sender]++;
        editionsSold[_articleId]++;

        // Calculate payments
        uint256 platformTake = (article.nftPrice * PLATFORM_TAKE_RATE) / 10000;
        uint256 journalistAmount = article.nftPrice - platformTake;

        // Send payments
        payable(article.author).transfer(journalistAmount);
        payable(TREASURY).transfer(platformTake + BUYER_FEE);

        // Generate reader licenses with edition number
        if (address(READER_LICENSE_AMM) != address(0)) {
            READER_LICENSE_AMM.mintLicensesToHolder(_articleId, msg.sender, article.readerLicenseRatio, editionNumber);
        }

        // Refund excess
        if (msg.value > totalPrice) {
            payable(msg.sender).transfer(msg.value - totalPrice);
        }

        emit NFTMinted(tokenId, _articleId, msg.sender, editionNumber, article.nftPrice, article.readerLicenseRatio);
    }

    // Generate pseudo-random index for edition selection
    function _getRandomIndex(uint256 _articleId) private view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(
    block.timestamp,
    block.prevrandao,  // <- CHANGED
    msg.sender,
    _articleId,
    editionsSold[_articleId]
)));
    }

    // Generate dynamic SVG for NFT
    function generateSVG(uint256 tokenId) public view returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        uint256 articleId = tokenToArticle[tokenId];
        uint256 editionNumber = tokenToEdition[tokenId];
        Article memory article = articles[articleId];

        // Color scheme based on category
        string memory primaryColor = "#2B3990"; // Default blockchain blue
        string memory secondaryColor = "#B3211E"; // Typewriter red

        if (keccak256(abi.encodePacked(article.category)) == keccak256("Politics")) {
            primaryColor = "#1D7F6E"; // Verification green
        } else if (keccak256(abi.encodePacked(article.category)) == keccak256("Tech")) {
            primaryColor = "#E8A317"; // Alert amber
        }

        return string(abi.encodePacked(
            '<svg width="400" height="400" xmlns="http://www.w3.org/2000/svg">',
            '<defs><linearGradient id="bg" x1="0%" y1="0%" x2="100%" y2="100%">',
            '<stop offset="0%" style="stop-color:', primaryColor, ';stop-opacity:1" />',
            '<stop offset="100%" style="stop-color:', secondaryColor, ';stop-opacity:1" />',
            '</linearGradient></defs>',
            '<rect width="400" height="400" fill="url(#bg)"/>',
            '<rect x="20" y="20" width="360" height="360" fill="rgba(255,255,255,0.1)" stroke="rgba(255,255,255,0.3)" stroke-width="2"/>',
            '<text x="200" y="80" text-anchor="middle" fill="white" font-family="serif" font-size="24" font-weight="bold">',
            'ImmutableType',
            '</text>',
            '<text x="200" y="120" text-anchor="middle" fill="rgba(255,255,255,0.9)" font-family="serif" font-size="16">',
            article.location,
            '</text>',
            '<text x="200" y="140" text-anchor="middle" fill="rgba(255,255,255,0.8)" font-family="serif" font-size="14">',
            'Edition #', editionNumber.toString(), '/', article.nftCount.toString(),
            '</text>',
            '<text x="200" y="160" text-anchor="middle" fill="rgba(255,255,255,0.8)" font-family="serif" font-size="14">',
            article.category,
            '</text>',
            '<foreignObject x="40" y="200" width="320" height="120">',
            '<div xmlns="http://www.w3.org/1999/xhtml" style="color:white;font-family:serif;font-size:12px;text-align:center;word-wrap:break-word;">',
            _truncateString(article.title, 100),
            '</div>',
            '</foreignObject>',
            '<text x="200" y="350" text-anchor="middle" fill="rgba(255,255,255,0.7)" font-family="serif" font-size="12">',
            'Article #', article.id.toString(),
            '</text>',
            '</svg>'
        ));
    }

    // Generate NFT metadata
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");

        uint256 articleId = tokenToArticle[tokenId];
        uint256 editionNumber = tokenToEdition[tokenId];
        Article memory article = articles[articleId];

        bytes memory dataURI = abi.encodePacked(
            '{',
                '"name": "', article.title, ' - Edition #', editionNumber.toString(), '",',
                '"description": "', article.summary, '",',
                '"image": "data:image/svg+xml;base64,', Base64.encode(bytes(generateSVG(tokenId))), '",',
                '"attributes": [',
                    '{"trait_type": "Location", "value": "', article.location, '"},',
                    '{"trait_type": "Category", "value": "', article.category, '"},',
                    '{"trait_type": "Author", "value": "', _addressToString(article.author), '"},',
                    '{"trait_type": "Edition Number", "value": "', editionNumber.toString(), '"},',
                    '{"trait_type": "Total Editions", "value": "', article.nftCount.toString(), '"},',
                    '{"trait_type": "Publication Date", "value": "', article.publishedAt.toString(), '"},',
                    '{"trait_type": "Reader Licenses Per NFT", "value": "', article.readerLicenseRatio.toString(), '"}',
                ']',
            '}'
        );

        return string(abi.encodePacked("data:application/json;base64,", Base64.encode(dataURI)));
    }

    // Get NFT holders for an article
    function getNFTHolders(uint256 _articleId) external view returns (address[] memory) {
        return articleNFTHolders[_articleId];
    }

    // Get edition number for a token
    function getEditionNumber(uint256 tokenId) external view returns (uint256) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        return tokenToEdition[tokenId];
    }

    // Get available editions for an article
    function getAvailableEditions(uint256 _articleId) external view returns (uint256[] memory) {
        return availableEditions[_articleId];
    }

    // Get articles by author
    function getArticlesByAuthor(address _author) external view returns (uint256[] memory) {
        uint256 totalArticles = _articleIdCounter;
        uint256 authorArticleCount = 0;

        // Count author's articles
        for (uint256 i = 1; i <= totalArticles; i++) {
            if (articles[i].author == _author) {
                authorArticleCount++;
            }
        }

        // Create array of author's article IDs
        uint256[] memory authorArticles = new uint256[](authorArticleCount);
        uint256 index = 0;

        for (uint256 i = 1; i <= totalArticles; i++) {
            if (articles[i].author == _author) {
                authorArticles[index] = i;
                index++;
            }
        }

        return authorArticles;
    }

    // Get total article count
    function getTotalArticles() external view returns (uint256) {
        return _articleIdCounter;
    }

    // Royalty distribution override
    function royaltyInfo(uint256 tokenId, uint256 salePrice) public view override returns (address, uint256) {
        uint256 articleId = tokenToArticle[tokenId];
        Article memory article = articles[articleId];

        uint256 royaltyAmount = (salePrice * 490) / 10000; // 4.9%
        uint256 creatorAmount = (royaltyAmount * 98) / 100; // 98% to creator

        return (article.author, creatorAmount);
    }

    // Utility functions
    function _truncateString(string memory str, uint256 maxLen) internal pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        if (strBytes.length <= maxLen) {
            return str;
        }

        bytes memory result = new bytes(maxLen);
        for (uint256 i = 0; i < maxLen; i++) {
            result[i] = strBytes[i];
        }
        return string(result);
    }

    function _addressToString(address _addr) internal pure returns (string memory) {
        bytes32 value = bytes32(uint256(uint160(_addr)));
        bytes memory alphabet = "0123456789abcdef";
        bytes memory str = new bytes(42);
        str[0] = '0';
        str[1] = 'x';
        for (uint256 i = 0; i < 20; i++) {
            str[2+i*2] = alphabet[uint8(value[i + 12] >> 4)];
            str[3+i*2] = alphabet[uint8(value[i + 12] & 0x0f)];
        }
        return string(str);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage, ERC721Royalty) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/content/ReaderLicenseAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface IEncryptedArticles {
    function articles(uint256 articleId) external view returns (
        uint256 id,
        string memory title,
        string memory encryptedContent,
        string memory summary,
        address author,
        string memory location,
        string memory category,
        string[] memory tags,
        uint256 publishedAt,
        uint256 nftCount,
        uint256 nftPrice,
        uint256 journalistRetained,
        uint256 readerLicenseRatio,
        uint8 creationSource,
        uint256 proposalId
    );
    function getNFTHolders(uint256 articleId) external view returns (address[] memory);
}

contract ReaderLicenseAMM is ERC1155, Ownable {
    // Constants
    address public constant TREASURY = 0x9402F9f20b4a27b55B1cC6cf015D98f764814fb2;
    uint256 public constant REGENERATION_THRESHOLD = 50; // 50% consumed triggers regeneration
    uint256 public constant BASE_PRICE = 0.01 ether; // 0.01 FLOW
    uint256 public constant MAX_PRICE = 1 ether; // 1.00 FLOW
    uint256 public constant GAS_REIMBURSEMENT = 0.005 ether; // 0.005 FLOW per regeneration call

    // Revenue split percentages
    uint256 public constant SELLER_PERCENTAGE = 70;
    uint256 public constant CREATOR_PERCENTAGE = 20;
    uint256 public constant PLATFORM_PERCENTAGE = 10;

    // Contract references
    IEncryptedArticles public immutable ENCRYPTED_ARTICLES;

    // Structs
    struct LicenseInfo {
        uint256 articleId;
        uint256 editionNumber;
        uint256 totalGenerated;
        uint256 activeLicenses;
        uint256 lastRegenerationTime;
    }

    struct AccessInfo {
        uint256 expiryTime;
        bool hasAccess;
    }

    struct PurchaseRecord {
        uint256 timestamp;
        uint256 price;
        address buyer;
        address seller;
    }

    // State variables
    uint256 private _licenseTypeIdCounter;

    // articleId => LicenseInfo
    mapping(uint256 => LicenseInfo) public licenseInfo;

    // articleId => address => license balance
    mapping(uint256 => mapping(address => uint256)) public licenseBalances;

    // articleId => address => AccessInfo
    mapping(uint256 => mapping(address => AccessInfo)) public accessInfo;

    // articleId => day => purchase count
    mapping(uint256 => mapping(uint256 => uint256)) public dailyPurchases;

    // articleId => PurchaseRecord[]
    mapping(uint256 => PurchaseRecord[]) public purchaseHistory;

    // articleId => total licenses ever created
    mapping(uint256 => uint256) public totalLicensesEverGenerated;

    // Gas pool for regeneration costs
    uint256 public gasPool;

    // Events
    event LicenseMinted(uint256 indexed articleId, address indexed holder, uint256 count, uint256 editionNumber);
    event LicensePurchased(uint256 indexed articleId, address indexed buyer, address indexed seller, uint256 price);
    event LicenseBurned(uint256 indexed articleId, address indexed user, uint256 expiryTime);
    event LicenseRegenerated(uint256 indexed articleId, uint256 count, uint256 totalActive);
    event AccessGranted(uint256 indexed articleId, address indexed user, uint256 expiryTime);

    constructor(address _encryptedArticles) ERC1155("") Ownable(msg.sender) {
        ENCRYPTED_ARTICLES = IEncryptedArticles(_encryptedArticles);
    }

    // Mint initial licenses to NFT holder (called by EncryptedArticles contract)
    function mintLicensesToHolder(
        uint256 _articleId, 
        address _holder, 
        uint256 _count, 
        uint256 _editionNumber
    ) external {
        require(msg.sender == address(ENCRYPTED_ARTICLES), "Only EncryptedArticles contract can mint");

        // Update license info
        licenseInfo[_articleId].articleId = _articleId;
        licenseInfo[_articleId].totalGenerated += _count;
        licenseInfo[_articleId].activeLicenses += _count;

        // Update total ever generated
        totalLicensesEverGenerated[_articleId] += _count;

        // Mint licenses to holder
        licenseBalances[_articleId][_holder] += _count;
        _mint(_holder, _articleId, _count, "");

        emit LicenseMinted(_articleId, _holder, _count, _editionNumber);
    }

    // Calculate current price based on supply-scaled bonding curve with time decay
    function getCurrentPrice(uint256 _articleId) public view returns (uint256) {
        uint256 totalSupply = totalLicensesEverGenerated[_articleId];
        require(totalSupply > 0, "No licenses exist for this article");

        // Get total licenses that will ever exist (from NFT count × license ratio)
        (, , , , , , , , , uint256 nftCount, , , uint256 readerLicenseRatio, ,) = ENCRYPTED_ARTICLES.articles(_articleId);
        uint256 maxTotalLicenses = nftCount * readerLicenseRatio;

        // Calculate base price using supply-scaled bonding curve
        uint256 priceRange = MAX_PRICE - BASE_PRICE; // 0.99 FLOW
        uint256 priceIncrement = maxTotalLicenses > 1 ? priceRange / (maxTotalLicenses - 1) : 0;
        uint256 bondingPrice = BASE_PRICE + ((totalSupply - 1) * priceIncrement);

        // Apply time decay
        uint256 decayedPrice = applyTimeDecay(_articleId, bondingPrice);

        return decayedPrice > MAX_PRICE ? MAX_PRICE : decayedPrice;
    }

    // Apply time decay based on zero-sale days
    function applyTimeDecay(uint256 _articleId, uint256 _currentPrice) internal view returns (uint256) {
        (, , , , , , , , uint256 publishedAt, , , , , ,) = ENCRYPTED_ARTICLES.articles(_articleId);
        uint256 daysSincePublish = (block.timestamp - publishedAt) / 86400;
        uint256 zeroSaleDays = 0;

        // Count consecutive days with zero sales (starting from most recent)
        uint256 currentDay = block.timestamp / 86400;
        for (uint256 i = 0; i < daysSincePublish && i < 30; i++) {
            if (dailyPurchases[_articleId][currentDay - i] == 0) {
                zeroSaleDays++;
            } else {
                break; // Stop at first day with sales
            }
        }

        // 1% decay per zero-sale day, max 30% decay
        uint256 decayPercent = zeroSaleDays > 30 ? 30 : zeroSaleDays;
        uint256 decayAmount = (_currentPrice * decayPercent) / 100;

        return _currentPrice > decayAmount ? _currentPrice - decayAmount : 0.001 ether; // Floor at 0.001 FLOW
    }

    // Purchase license from another holder
    function buyLicense(uint256 _articleId, address _seller) external payable {
        require(licenseBalances[_articleId][_seller] > 0, "Seller has no licenses");

        uint256 currentPrice = getCurrentPrice(_articleId);
        uint256 estimatedGas = GAS_REIMBURSEMENT;
        uint256 totalCost = currentPrice + estimatedGas;

        require(msg.value >= totalCost, "Insufficient payment");

        // Calculate revenue splits from license price only
        uint256 sellerAmount = (currentPrice * SELLER_PERCENTAGE) / 100;
        uint256 creatorAmount = (currentPrice * CREATOR_PERCENTAGE) / 100;
        uint256 platformAmount = currentPrice - sellerAmount - creatorAmount;

        // Get article author for creator payment
        (, , , , address author, , , , , , , , , ,) = ENCRYPTED_ARTICLES.articles(_articleId);

        // Transfer license
        licenseBalances[_articleId][_seller]--;
        licenseBalances[_articleId][msg.sender]++;
        _safeTransferFrom(_seller, msg.sender, _articleId, 1, "");

        // Process payments
        payable(_seller).transfer(sellerAmount);
        payable(author).transfer(creatorAmount);
        payable(TREASURY).transfer(platformAmount);

        // Add gas contribution to pool
        gasPool += estimatedGas;

        // Update purchase tracking
        uint256 today = block.timestamp / 86400;
        dailyPurchases[_articleId][today]++;
        purchaseHistory[_articleId].push(PurchaseRecord({
            timestamp: block.timestamp,
            price: currentPrice,
            buyer: msg.sender,
            seller: _seller
        }));

        // Refund excess
        if (msg.value > totalCost) {
            payable(msg.sender).transfer(msg.value - totalCost);
        }

        emit LicensePurchased(_articleId, msg.sender, _seller, currentPrice);

        // Check if regeneration needed
        if (shouldRegenerate(_articleId)) {
            _regenerateLicenses(_articleId);
        }
    }

    // Burn license for 7-day article access
    function burnLicenseForAccess(uint256 _articleId) external {
        require(licenseBalances[_articleId][msg.sender] > 0, "No license to burn");
        require(!hasActiveAccess(_articleId, msg.sender), "Already has active access");

        // Burn the license
        licenseBalances[_articleId][msg.sender]--;
        licenseInfo[_articleId].activeLicenses--;
        _burn(msg.sender, _articleId, 1);

        // Grant 7-day access
        uint256 expiryTime = block.timestamp + 7 days;
        accessInfo[_articleId][msg.sender] = AccessInfo({
            expiryTime: expiryTime,
            hasAccess: true
        });

        emit LicenseBurned(_articleId, msg.sender, expiryTime);
        emit AccessGranted(_articleId, msg.sender, expiryTime);

        // Check if regeneration needed
        if (shouldRegenerate(_articleId)) {
            _regenerateLicenses(_articleId);
        }
    }

    // Check if regeneration is needed (50% threshold)
    function shouldRegenerate(uint256 _articleId) public view returns (bool) {
        uint256 totalGenerated = licenseInfo[_articleId].totalGenerated;
        uint256 activeLicenses = licenseInfo[_articleId].activeLicenses;

        if (totalGenerated == 0) return false;

        uint256 consumedPercentage = ((totalGenerated - activeLicenses) * 100) / totalGenerated;
        return consumedPercentage >= REGENERATION_THRESHOLD;
    }

    // Regenerate licenses (anyone can call, gas reimbursed)
    function regenerateLicenses(uint256 _articleId) external {
        require(shouldRegenerate(_articleId), "Regeneration not needed");
        require(gasPool >= GAS_REIMBURSEMENT, "Insufficient gas pool");

        _regenerateLicenses(_articleId);

        // Reimburse caller for gas
        gasPool -= GAS_REIMBURSEMENT;
        payable(msg.sender).transfer(GAS_REIMBURSEMENT);
    }

    // Internal regeneration logic
    function _regenerateLicenses(uint256 _articleId) internal {
        uint256 totalGenerated = licenseInfo[_articleId].totalGenerated;
        uint256 activeLicenses = licenseInfo[_articleId].activeLicenses;
        uint256 deltaToRegenerate = totalGenerated - activeLicenses;

        if (deltaToRegenerate == 0) return;

        // Get NFT holders for round-robin distribution
        address[] memory nftHolders = ENCRYPTED_ARTICLES.getNFTHolders(_articleId);
        require(nftHolders.length > 0, "No NFT holders");

        // Distribute licenses round-robin
        for (uint256 i = 0; i < deltaToRegenerate; i++) {
            address recipient = nftHolders[i % nftHolders.length];
            licenseBalances[_articleId][recipient]++;
            _mint(recipient, _articleId, 1, "");
        }

        // Update license info
        licenseInfo[_articleId].activeLicenses += deltaToRegenerate;
        licenseInfo[_articleId].lastRegenerationTime = block.timestamp;
        totalLicensesEverGenerated[_articleId] += deltaToRegenerate;

        emit LicenseRegenerated(_articleId, deltaToRegenerate, licenseInfo[_articleId].activeLicenses);
    }

    // Check if user has active access to article
    function hasActiveAccess(uint256 _articleId, address _user) public view returns (bool) {
        AccessInfo memory access = accessInfo[_articleId][_user];
        return access.hasAccess && block.timestamp < access.expiryTime;
    }

    // Get encrypted article content (only for users with active access)
    function getEncryptedContent(uint256 _articleId) external view returns (string memory) {
        require(hasActiveAccess(_articleId, msg.sender), "No active access to this article");

        (, , string memory encryptedContent, , , , , , , , , , , ,) = ENCRYPTED_ARTICLES.articles(_articleId);
        return encryptedContent;
    }

    // Get article summary (public)
    function getArticleSummary(uint256 _articleId) external view returns (string memory) {
        (, , , string memory summary, , , , , , , , , , ,) = ENCRYPTED_ARTICLES.articles(_articleId);
        return summary;
    }

    // Get license holders for an article
    function getLicenseHolders(uint256 _articleId) external view returns (address[] memory, uint256[] memory) {
        address[] memory nftHolders = ENCRYPTED_ARTICLES.getNFTHolders(_articleId);
        uint256[] memory balances = new uint256[](nftHolders.length);

        for (uint256 i = 0; i < nftHolders.length; i++) {
            balances[i] = licenseBalances[_articleId][nftHolders[i]];
        }

        return (nftHolders, balances);
    }

    // Get purchase history for an article
    function getPurchaseHistory(uint256 _articleId) external view returns (PurchaseRecord[] memory) {
        return purchaseHistory[_articleId];
    }

    // Emergency functions
    function emergencyWithdrawGasPool() external onlyOwner {
        uint256 amount = gasPool;
        gasPool = 0;
        payable(owner()).transfer(amount);
    }

    // Required ERC1155 overrides
    function uri(uint256 tokenId) public view override returns (string memory) {
        (, string memory title, , string memory summary, , string memory location, , , , , , , , ,) = ENCRYPTED_ARTICLES.articles(tokenId);

        return string(abi.encodePacked(
            '{"name": "Reader License - ', title, '",',
            '"description": "', summary, '",',
            '"attributes": [',
                '{"trait_type": "Location", "value": "', location, '"},',
                '{"trait_type": "License Type", "value": "Reader Access"}',
            ']}'
        ));
    }
}

